import { Component } from '@angular/core';
import {Student} from "../../model/Student";

@Component({
  selector: 'app-student-form',
  templateUrl: './student-form.component.html',
  styleUrls: ['./student-form.component.css']
})
export class StudentFormComponent {
  id = ''
  name = ''
  course = ''
  students: Student[] = []

  onAddStudent() {
    this.students.push(new Student(this.id, this.name, this.course))
    console.log(this.students)
  }
}
<div class="row">
  <div class="col-2">
    <input type="text" class="form-control" placeholder="Id" aria-label="First name" [(ngModel)]="id">
  </div>
  <div class="col-2">
    <input type="text" class="form-control" placeholder="Name" aria-label="Last name" [(ngModel)]="name">
  </div>
  <div class="col-2">
    <input type="text" class="form-control" placeholder="Course" aria-label="Last name" [(ngModel)]="course">
  </div>
  <button type="button" class="btn btn-primary col-2" (click)="onAddStudent()">Add Student</button>
</div>
